import { useState } from 'react';

function IdEntry({ setStage, setStudent, setQuestions }) {
  const [id, setId] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async () => {
    const res = await fetch('http://localhost:5000/api/validate-id', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ id })
    });
    const data = await res.json();
    if (data.success) {
      if (window.confirm(`Confirm: ${data.name}, ${data.domain}`)) {
        setStudent({ id, name: data.name, domain: data.domain });
        const qRes = await fetch('http://localhost:5000/api/questions', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ domain: data.domain })
        });
        setQuestions(await qRes.json());
        setStage('test');
      }
    } else {
      setError('Invalid ID');
    }
  };

  return (
    <div className="mt-5">
      <h2>Enter Your ID</h2>
      <input
        type="text"
        className="form-control"
        value={id}
        onChange={(e) => setId(e.target.value)}
        placeholder="Enter ID"
      />
      {error && <p className="text-danger">{error}</p>}
      <button className="btn btn-primary mt-3" onClick={handleSubmit}>
        Submit
      </button>
    </div>
  );
}

export default IdEntry;
